import React from "react";
import { NavLink, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";
import "./Sidebar.css";   // ‚¨ÖÔ∏è l√§gg till denna

export default function Sidebar() {
    const { user, logout } = useAuth();
    const navigate = useNavigate();

    const handleLogout = async () => {
        try {
            await logout();
        } finally {
            navigate("/login");
        }
    };

    return (
        <aside className="sidebar">
            <h3>Matchningssystem</h3>

            <ul className="nav flex-column">
                <li className="nav-item mb-2">
                    <NavLink className="nav-link" to="/">üè† √ñversikt</NavLink>
                </li>

                <li className="nav-item mb-2">
                    <NavLink className="nav-link" to="/anvandare">üë• Anv√§ndare</NavLink>
                </li>

                <li className="nav-item mb-2">
                    <NavLink className="nav-link" to="/kompetenser">üß† Kompetenser</NavLink>
                </li>

                <li className="nav-item mb-2">
                    <NavLink className="nav-link" to="/projekt">üìÅ Projekt</NavLink>
                </li>

                <li className="nav-item mb-2">
                    <NavLink className="nav-link" to="/team">üíº Team</NavLink>
                </li>

                <li className="nav-item mb-2">
                    <NavLink className="nav-link" to="/profile">üë§ Profil</NavLink>
                </li>
            </ul>

            <div style={{ marginTop: "auto", paddingTop: 16 }}>
                {user && <div style={{ fontSize: 12, opacity: 0.8, marginBottom: 8 }}>Inloggad som<br />{user.name} ({user.email})</div>}
                <button className="nav-link" onClick={handleLogout} style={{ background: "none", border: 0, padding: 0, cursor: "pointer", color: "inherit" }}>
                    üö™ Logga ut
                </button>
            </div>
        </aside>
    );
}
